import UIKit

extension GetConnectorsWorker: ApplicationUser {
	
}

class GetConnectorsWorker {
	// MARK: Business Logic
	
	func getConnectorTypes() -> [ConnectorType] {
		if let application = application {
			return application.getConnectorsTypes()
		}
		return [ConnectorType]()
	}
	
	func getConnectorsByType() -> [ConnectorType: [Connector]] {
		if let application = application {
			return application.getConnectorsByType()
		}
		return [ConnectorType: [Connector]]()
	}
}
